## Definición de funciones que llevan del angulo a las coordenadas x y 

def intensidades_desde_angulos(gR: float, gV: float, gA: float,
                               coeffs: Dict[str, Sequence[float]]
                              ) -> Tuple[np.ndarray, np.ndarray, np.ndarray]:
    """Devuelve IR, IV, IA. Si los coeficientes son escalares, devuelven escalares; 
    si son arrays (por longitud de espectro) devuelven arrays."""
    def quad(params, x):
        a, b, c = params
        # a,b,c pueden ser escalares o arrays; se devuelve array si alguno es array
        return a * x**2 + b * x + c

    IR = quad(coeffs['R'], gR)
    IV = quad(coeffs['V'], gV)
    IA = quad(coeffs['A'], gA)
    return np.asarray(IR), np.asarray(IV), np.asarray(IA)


def _channel_sum(spectrum: np.ndarray, intensity: np.ndarray) -> float:
    """Multiplica spectrum * intensity y hace la suma, con checks de forma."""
    spec = np.asarray(spectrum)
    inten = np.asarray(intensity)

    # Si intensidad es escalar -> broadcasting con spectrum ok
    if inten.ndim == 0:
        return np.sum(spec * float(inten))

    # Si intensidad es array: deben coincidir las formas para multiplicar elementwise
    if spec.shape != inten.shape:
        raise ValueError(f"Shapes incompatibles: spectrum {spec.shape} vs intensity {inten.shape}.")
    return float(np.sum(spec * inten))


def XYZ_desde_intensidades(IR, IV, IA,
                           xs: Dict[str, np.ndarray],
                           ys: Dict[str, np.ndarray],
                           zs: Dict[str, np.ndarray],
                           scale_factors: Tuple[float, float, float] = (1.0, 1.0, 1.0),
                           prefactor: float = 2.0
                          ) -> Tuple[float, float, float]:
    """
    Calcula X,Y,Z. Maneja IR/IV/IA escalares o arrays.
    xs,ys,zs deben ser dicts {'R':xs_R, 'V':xs_V, 'A':xs_A} con arrays.
    """
    sR, sV, sA = scale_factors

    IRs = np.asarray(IR) * sR
    IVs = np.asarray(IV) * sV
    IAs = np.asarray(IA) * sA

    # Para cada canal hacemos sum(spectrum * intensidad) — esto siempre da un escalar por canal
    XR = _channel_sum(xs['R'], IRs)
    XV = _channel_sum(xs['V'], IVs)
    XA = _channel_sum(xs['A'], IAs)
    X = prefactor * (XR + XV + XA)

    YR = _channel_sum(ys['R'], IRs)
    YV = _channel_sum(ys['V'], IVs)
    YA = _channel_sum(ys['A'], IAs)
    Y = prefactor * (YR + YV + YA)

    ZR = _channel_sum(zs['R'], IRs)
    ZV = _channel_sum(zs['V'], IVs)
    ZA = _channel_sum(zs['A'], IAs)
    Z = prefactor * (ZR + ZV + ZA)

    return X, Y, Z


def xy_desde_angulos(gR: float, gV: float, gA: float,
                     coeffs: Dict[str, Sequence[float]],
                     xs: Dict[str, np.ndarray],
                     ys: Dict[str, np.ndarray],
                     zs: Dict[str, np.ndarray],
                     scale_factors: Tuple[float, float, float] = (1.0, 1.0, 1.0),
                     prefactor: float = 2.0
                    ) -> Tuple[float, float]:
    IR, IV, IA = intensidades_desde_angulos(gR, gV, gA, coeffs)
    X, Y, Z = XYZ_desde_intensidades(IR, IV, IA, xs, ys, zs, scale_factors, prefactor)
    total = X + Y + Z
    if total == 0:
        return float('nan'), float('nan')
    return X / total, Y / total

